package operator;

public class Ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		a += 1; // a = a+ 1
		a = a++;
		System.out.println(a++); // 11
		System.out.println(++a); // 13
		System.out.println(a++); // 13
	}
	// prefix, suffix
	// --Tien to nam truoc : ++a
	// ---------hau to nam sau: a++

}
